<?php  $workingdir = $_SERVER["DOCUMENT_ROOT"].'/dbconn.php';  include($workingdir);if(isset($_POST["Province"]) && !empty($_POST["Province"])){   $query = $conn->query("SELECT DISTINCT Code2, District FROM wp_store_address WHERE Code1 = ".$_POST['Province']." ORDER BY District ASC");   $rowCount = $query->num_rows;    if($rowCount > 0){        echo '<option value="">District*</option>';        while($row = $query->fetch_assoc()){             echo '<option value="'.$row['Code2'].'">'.$row['District'].'</option>';        }    }else{        echo '<option value="">District not available</option>';    }}if(isset($_POST["district_id"]) && !empty($_POST["district_id"])){    $query = $conn->query("SELECT * FROM wp_store_address WHERE Code2 = ".$_POST['district_id']."  ORDER BY Ward ASC");    $rowCount = $query->num_rows;     if($rowCount > 0){        echo '<option value="">Ward*</option>';        while($row = $query->fetch_assoc()){             echo '<option value="'.$row['Code3'].'">'.$row['Ward'].'</option>';        }    }else{        echo '<option value="">Ward not available</option>';    }}?>