<?phpClass sendMail {	public function customSendmail($usermail = null ,$userphone = null, $consumer_id = null, $subject = null, $mailtype = null, $encoded = null, $neworderid = null, $delivery_date = null, $myproduct = null, $storedomain = null, $storeaddress = null, $storeprovince = null, $storedist = null, $storeward = null, $ordertime = null) {	require_once( ABSPATH . WPINC . '/class-phpmailer.php' );	$mail = new PHPMailer();	$charset = get_bloginfo('charset');	$mail->CharSet = $charset;	$swpsmtp_options = get_option('swpsmtp_options');	$from_name = $swpsmtp_options['from_name_field'];	$from_email = $swpsmtp_options['from_email_field'];	$mail->IsSMTP();	/* If using smtp auth, set the username & password */	if ('yes' == $swpsmtp_options['smtp_settings']['autentication']) {	$mail->SMTPAuth = true;	$mail->Username = $swpsmtp_options['smtp_settings']['username'];	$mail->Password = swpsmtp_get_password();	}	/* Set the SMTPSecure value, if set to none, leave this blank */	if ($swpsmtp_options['smtp_settings']['type_encryption'] !== 'none') {	$mail->SMTPSecure = $swpsmtp_options['smtp_settings']['type_encryption'];	}	/* PHPMailer 5.2.10 introduced this option. However, this might cause issues if the server is advertising TLS with an invalid certificate. */	$mail->SMTPAutoTLS = false;	/* Set the other options */	$mail->Host = $swpsmtp_options['smtp_settings']['host'];	$mail->Port = $swpsmtp_options['smtp_settings']['port'];	$mail->SetFrom($from_email, $from_name);	$mail->isHTML(true);	/* $logo = get_stylesheet_directory_uri().'/images/logo.png'; */	$faq_url = get_permalink(1428);	$site_url= get_site_url();	$reset_passurl = get_permalink(1417)."?activationkey=".$encoded;	/* $fb = get_stylesheet_directory_uri().'/images/newfb.png';	$ping = get_stylesheet_directory_uri().'/images/newping.png'; */	$logo = 'https://webnhathuoc.vn/wp-content/themes/medican-child/images/logo.png';	$fb = 'https://webnhathuoc.vn/wp-content/themes/medican-child/images/newfb.png';	$ping = 'https://webnhathuoc.vn/wp-content/themes/medican-child/images/newping.png';	$message = "<div style='float:left;width:100%;'>	<img style='text-align:center; padding-bottom: 10px;' src='".$logo."'></br>	<div class='top' style='padding-top: 15px; border-top: 5px solid #4abdac;float: left; width: 100%;'>	<table class='body-wrap'>	<tr>	<td><h3 style='color:black;'>Hi, ".$userphone."</h3>";	if($mailtype=='user_register')	{	$message .="<p style='color:black;'>Thanks for your registration. Your phone to sign in is ".$userphone.", your email to receive notifications is ".$usermail.", and your Consumer ID to make transactions is ".$consumer_id.".</p>";	$buttonurl = 'Go to Homepage';	$redirecturl = $site_url;	}	if($mailtype=='forgot_password')	{	$message .="<p style='color:black;'>Please go to the below Reset Password Button for  Reset the Password.</p>";	$buttonurl = 'Reset Password';	$redirecturl = $reset_passurl;	}	if($mailtype=='user_order')	{	$message .="<p style='color:black;'>Thank you for getting in touch. We have received your Order with order id is ".$neworderid." sent at ".$ordertime.".</p>";	$message .="<table><tr><td style='border: 1px solid gray;padding: 10px 20px;'>Pharmacy name</td><td style='border: 1px solid gray;padding: 10px 20px;'>".$myproduct."</td></tr><tr><td style='border: 1px solid gray;padding: 10px 20px;'>Delivery time</td><td style='border: 1px solid gray;padding: 10px 20px;'>".$delivery_date."</td></tr><tr><td style='border: 1px solid gray;padding: 10px 20px;'>Pharmacy domain</td><td style='border: 1px solid gray;padding: 10px 20px;'>".$storedomain."</td></tr><tr><td style='border: 1px solid gray;padding: 10px 20px;'>Pharmacy address</td><td style='border: 1px solid gray;padding: 10px 20px;'>".$storeaddress.','.$storeward.','.$storedist.','.$storeprovince.   "</td></tr><tr><td style='border: 1px solid gray;padding: 10px 20px;'>Pharmacy Phone</td><td style='border: 1px solid gray;padding: 10px 20px;'>0000000000</td></tr></table>";	$buttonurl = 'Go to Homepage';	$redirecturl = $site_url;	}	$message .="<p style='color:black;'>Phasellus dictum sapien a neque luctus cursus. Pellentesque sem dolor, fringilla et pharetra vitae. consequat vel lacus. Sed iaculis pulvinar ligula, ornare fringilla ante viverra et. In hac habitasse platea dictumst. Donec vel orci mi, eu congue justo. Integer eget odio est, eget malesuada lorem. Aenean sed tellus dui, vitae viverra risus. Nullam massa sapien, pulvinar eleifend fringilla id, convallis eget nisi. Mauris a sagittis dui. Pellentesque non lacinia mi. Fusce sit amet libero sit amet erat venenatis sollicitudin vitae vel eros. Cras nunc sapien, interdum sit amet porttitor ut, congue quis urna.</p>	<div style='text-align:center;margin-bottom: 15px; margin-top: 30px;'><a style=' text-decoration: none; text-transform: uppercase; background: #f7b733 none repeat scroll 0 0; border-radius: 5px;color: white;float: none; font-family: sans-serif; font-size: 12px; padding: 10px 20px; text-align: center;' href='".$redirecturl."'>".$buttonurl."</a>	</div>	<table style=' margin-top: 18px; background: #ebebeb none repeat scroll 0 0;float: left;width: 100%;'>	<tr>	<td>	<table style='float: left;width: 50%;'>	<tbody>	<tr>	<td style='padding: 0px 15px;'>	<h5 style='margin-bottom: 0; font-size: 17px; font-weight: 900; color: black; margin-top:10px;'>Connect with Us:</h5>	<p style='margin-bottom: 0;'>	<a href='https://www.facebook.com/webnhathuocvn-759849100851664'><img style='text-align:center; padding-bottom: 10px; height: 30px; width:30px;' src='".$fb."'></a>	</p>	</td>	</tr>	</tbody>	</table>	<table style='float: left;width: 50%;'>	<tbody>	<tr>	<td style='padding: 0px 15px;'>	<h5 style='margin-bottom:0;font-size:17px;font-weight:900;color:black; margin-top:10px;'>Contact Info:</h5>	<p style='color:black; margin-bottom: 0;'>		<strong> Address:</strong>		Tầng 2, Số 02 &ndash; 04 Alexandre De Rhodes, Phường Bến Nghé, Quận 1, TP. Hồ Chí Minh, Việt Nam.	</p>	</td>	</tr>	</tbody>	</table>	</td>	</tr>	</table>	<table style='width: 100%; padding-bottom: 15px; padding-top: 15px;'>	<tbody>	<tr>	<td align='center'>	<p>	<a style='color:#2ba6cb;' href='".$faq_url."/#aboutus'>About us</a>	|	<a style='color:#2ba6cb;'  href='".$faq_url."/#term'>Terms and Conditions</a>	|	<a style='color:#2ba6cb;' href='".$faq_url."/#category1'>FAQs</a>	</p>	</td>	</tr>	</tbody>	</table>	</td>	</tr>	</table>	</div>	</div>";	$mail->Subject = $subject;	$mail->MsgHTML($message);	$mail->AddAddress($usermail);	$mail->SMTPDebug = 0;	/* Send mail and return result */	if (!$mail->Send())	$errors = $mail->ErrorInfo;	$mail->ClearAddresses();	$mail->ClearAllRecipients();	if (!empty($errors)){	return $errors;	} else{	return 'Email was sent';	}	}}?>