<?php$term = trim(strip_tags($_GET['term'])); $a_json = array();$a_json_row = array();$parse_uri = explode( 'wp-content', $_SERVER['SCRIPT_FILENAME'] );require_once( $parse_uri[0] . 'wp-load.php' );$args = array(              'product_cat' => 'Cardiologist',              'post_type' => 'product',              'orderby' => 'title',			  's' => $term,      );$query = new WP_Query( $args );    while ( $query->have_posts() ) {      $query->the_post();      $post_title =  htmlentities(stripslashes( get_the_title()));      $code = htmlentities(stripslashes(get_the_id()));      $a_json_row["id"] = $code;      $a_json_row["label"] = $post_title;      $a_json_row["value"] = $post_title;      array_push($a_json, $a_json_row);    }echo json_encode($a_json);flush();    wp_reset_postdata();?>