<?php/** * Login Form * * This template can be overridden by copying it to yourtheme/woocommerce/myaccount/form-login.php. * * HOWEVER, on occasion WooCommerce will need to update template files and you (the theme developer). * will need to copy the new files to your theme to maintain compatibility. We try to do this. * as little as possible, but it does happen. When this occurs the version of the template file will. * be bumped and the readme will list any important changes. * * @see 	    http://docs.woothemes.com/document/template-structure/ * @author 		WooThemes * @package 	WooCommerce/Templates * @version     10.0.0 */if (!defined('ABSPATH')) {    exit; // Exit if accessed directly}?><script type='text/javascript'>function loginajax(){    jQuery("#preloader").css('display','block'); jQuery("#status").css('display','block'); var loginphone = jQuery('.loginPhone').val();var loginpass = jQuery('.loginpassword').val();var captcha_code = jQuery('#logincaptchacode').val();var radioValue = jQuery("input[name='login']:checked").val();   jQuery.ajax({             type: "POST",             url: "<?php echo get_stylesheet_directory_uri().'/woocommerce/myaccount/loginajax.php' ?>",             data: {loginphone:loginphone, loginpass:loginpass, captcha_code: captcha_code ,radioValue: radioValue},             dataType: 'json',             success: function(result){           jQuery("#preloader").css('display','none');            jQuery("#status").css('display','none');                   if(result['error']==1)                      {                          jQuery('.loginerror').html(result['message']);                            jQuery('#logincaptchaimg').attr('src',' <?php echo plugins_url().'/custom-frontend-login-registration-form/captcha.php'?>?'+"?rand="+Math.random()*1000);                        if(result['counter'])                            {                                if(result['counter']>4)                                    {                                   jQuery('.captcha').css("display","block");                                          }                                   else                                     {                                   jQuery('.captcha').css("display","none");                                      }                             }                        }                                                      if(result['error']==0)                      {                        jQuery('.customtrigger').trigger('click');                          jQuery("#preloader").css('display','block');                         jQuery("#status").css('display','block');                                              if('<?php echo is_page(1320)?>')                        {                          window.location.assign("<?php echo get_site_url(); ?>");                        }                        else{                          window.location.assign("");                         }                      }                }            });} jQuery(document).ready(function () {  jQuery('.customclasslogin ').on('input', function() {           jQuery(".loginerror").html('');        });         jQuery('#customloginform').validate({ // initialize the plugin                        rules: {                            loginPhone: {                                required: true,                                                           },                            loginpassword: {                              required: true,                               },                            logincaptchacode: {                               required: true,                                        },                         },                                                submitHandler: function (form) { // for demo                           loginajax();                        }                    });                                    });</script><!--forgot password--><script type='text/javascript'>    function forgotajax(){jQuery("#preloader").css('display','block'); jQuery("#status").css('display','block');     var loginphone = jQuery('#forgotphone').val();var captcha_code = jQuery('#forgotcaptcha_code').val(); jQuery.ajax({             type: "POST",             url: "<?php echo get_stylesheet_directory_uri().'/woocommerce/myaccount/forgotajax.php' ?>",             data: {loginphone:loginphone, captcha_code: captcha_code },             dataType: 'json',             success: function(result){                   jQuery("#preloader").css('display','none');                    jQuery("#status").css('display','none');                                          if(result['error'] && result['error']==1)                      {                        jQuery('.forgoterror').html(result['message']);                         jQuery('#forgotcaptchaimg').attr('src',' <?php echo plugins_url().'/custom-frontend-login-registration-form/captcha.php'?>?'+"?rand="+Math.random()*1000);                        if(result['counter']>4)                            {                        jQuery('.forgotcaptcha').css("display","block");                                }                      }                   else                       {                      jQuery('.forgoterror ').html('');                        document.getElementById("forgotform").reset();					  jQuery("#preloader").css('display','block'); 					  jQuery("#status").css('display','block');                                            if('<?php echo is_page(1320)?>')                                                {                                                  window.location.assign("<?php echo get_site_url(); ?>");                                                }                                                else{                                                  window.location.assign("");                                                 }					  jQuery('.customtrigger').trigger('click');                        }                                     }            });}jQuery(document).ready(function () {jQuery(document).keydown(function(e) {    // ESCAPE key pressed close the popup    if (e.keyCode == 27) {       if(jQuery('#login_form').is(":visible"))        {            jQuery('.customtrigger').trigger('click');        }              }     });jQuery('.customclassforgot ').on('input', function() {           jQuery(".forgoterror").html('');        });         jQuery('.customtrigger').click(function()    {     jQuery('.logindata').css("display","block");      jQuery('.forgot_pass_outer').css("display","none");      document.getElementById("forgotform").reset();     document.getElementById("customloginform").reset();     jQuery('.forgoterror ').html('');      jQuery(".loginerror").html('');     jQuery(".error").html('');//           });    jQuery('#forget_pass').click(function()    {        jQuery('.logindata').css("display","none");        jQuery('.forgot_pass_outer').css("display","block");    });  jQuery('#forgotform').validate({ // initialize the plugin                        rules: {                            forgotphone: {                                required: true,                                                           },                            forgotcaptchacode: {                               required: true,                                        },                         },                                                submitHandler: function (form) { // for demo                           forgotajax();                        }                    });    });</script><?php wc_print_notices(); ?><?php do_action('woocommerce_before_customer_login_form'); ?><?php if (get_option('woocommerce_enable_myaccount_registration') === 'yes') : ?><!--<div class='close-cross'><img class='crossimg' src='<?php // echo get_stylesheet_directory_uri().'/images/iconclose.png'?>'></div>-->    <div class="col2-set" id="customer_login">        <div class="col-1 logindata">        <?php endif; ?>        <!--<h2><?php // esc_html_e('Login', 'woocommerce'); ?></h2>-->        <form method="post" class="login" id="customloginform">            <?php do_action('woocommerce_login_form_start'); ?>            <div class='outer_login'>                <div class='innerouter_login'>                    <div class='header_login'>                        <ul>                            <li>I am a</li>                            <li><input checked type='radio' name='login' id='loginfirst' value="customer"> <span class="store_span">Consumer</span></li>                            <li class="asstore"><input type='radio' name='login' id='loginsecond' value="vendor"> <span class="store_span"> Store</span></li>                        </ul>                    </div>                  <div class='login_data top_formcontent'>                    <ul>                        <li>                         <input type="text" placeholder="Phone*" class="input-text loginPhone customclasslogin form-control login-field" name="loginPhone" id="username" value="<?php if (!empty($_POST['username'])) echo esc_attr($_POST['username']); ?>" />                              </li>                        <li>                        <input placeholder="Password*" class="input-text loginpassword customclasslogin form-control login-field" type="password" name="loginpassword" id="password" />                            </li>                       <li style='display:none' class='captcha'>                         <div class="captchadiv">                            <div class="captcha_main">                                <ul>                                                           <li class="captchaimage1">  <img src="<?php echo plugins_url().'/custom-frontend-login-registration-form/captcha.php'?>" id="logincaptchaimg"></li>                         <li class="captchaimage2">   <input class="customclasslogin form-control login-field" id="logincaptchacode" name="logincaptchacode" type="text" placeholder="Results*"></li>                                </ul>                            </div>                                                                                                                                                                         </div>                       </li>                       <li>                         <input class="login_in registerlink" type="submit" name="loginin" value="Sign in"/>                        </li>					    <span class="loginerror"></span>                       <li>                        <input id ='forget_pass' class="btn code_submit" type="button" name="forgotpass" value="Forgot Password?"/>                          </li>                                          </ul><!--                  <div class="bottom_span">                    <span class="more_info_login">                    Do not have an account?                     <a href="<?php // echo get_permalink(1320); ?>">Sign up as a Consumer account</a>                    </span>                    </div>    -->                  </div>               </div>                                                            </div>              <?php do_action('woocommerce_login_form'); ?>            <p class="form-row">                <?php wp_nonce_field('woocommerce-login', 'woocommerce-login-nonce'); ?>                <!--<input type="submit" class="button" name="login" value="<?php // esc_attr_e('Login', 'woocommerce'); ?>" />-->                <input name="rememberme" type="checkbox" id="rememberme" value="forever" />                <!--<label for="rememberme" class="inline">-->                    <?php // esc_html_e('Remember me', 'woocommerce'); ?>                <!--</label>-->            </p><!--            <p class="lost_password">                <a href="<?php // echo esc_url(wp_lostpassword_url()); ?>"><?php // esc_html_e('Lost your password?', 'woocommerce'); ?></a>            </p>-->            <?php do_action('woocommerce_login_form_end'); ?>        </form>        <?php if (get_option('woocommerce_enable_myaccount_registration') === 'yes') : ?>        </div>        <div class="forgot_pass_outer" style="display:none">             <form method="post" class="forgotform" id="forgotform">                <div class="forgot_pass_inner top_formcontent">                    <ul>                         <li class="top1 checkmail">                           <!--<h3>Reset Password</h3>-->                            <span class="content">Please Check your email</span>                         </li>                         <li class="top1">                           <input id="forgotphone" class="form-control login-field customclassforgot" type="text" placeholder="Phone*" value="" name="forgotphone">                          </li>                         <li style='display:none' class='forgotcaptcha '>                               <div class="captchadiv">                                    <div class="captcha_main">                                     <ul>                                   <li class="captchaimage1">  <img src="<?php echo plugins_url().'/custom-frontend-login-registration-form/captcha.php'?>" id="forgotcaptchaimg"></li>                                    <li class="captchaimage2"> <input id="forgotcaptcha_code" class="customclassforgot form-control login-field" name="forgotcaptchacode" type="text" placeholder="Results*"></li>                                    </ul>                                    </div>                                </div>                           </li>                         <li>                            <input class="btn btn-primary resetpasslink registerlink" type="submit" value="Reset Password" name="resetpasslink">                        </li>                        <span class="forgoterror"></span>                    </ul>                  </div>             </form>        </div>      </div><?php endif; ?><?php do_action('woocommerce_after_customer_login_form'); ?>