<?phpsession_start();$parse_uri = explode('wp-content', $_SERVER['SCRIPT_FILENAME']);require_once( $parse_uri[0] . 'wp-load.php' );if(isset($_POST['activationkey'])){$userpassword = $_POST['newpassword'];$useractivation = $_POST['activationkey'];global $wpdb;$results= $wpdb->get_results("select  * from `wp_users` where user_activation_key = $useractivation",ARRAY_A);$userid = $results[0]['ID'];wp_set_password($userpassword, $userid);$sql ="UPDATE wp_users  SET `user_activation_key`= ' ' WHERE  `user_activation_key` = '".$useractivation."'";$rez = $wpdb->query($sql);}?>